mapper.select:
SELECT aa.id as agreementAuditId,
aa.lawyer_id as firstLawyerId,
aa.end_lawyer_id as endLawyerId,
aa.state as getState,
agree.id as agreeId,
agree.name as agreeName,
agree.state as agreeState,
agree.end_time as endTime,
cus.name as customName
FROM bus_agreement_audit aa
left join bus_agreement agree on agree.id = aa.agreement_id
left join bus_custom cus on cus.id = agree.custom_id
where aa.is_delete = 0

    and agree.state = #{ condition.state }



    and agree.name like #{ name }



         and aa.lawyer_id = #{ condition.lawyerId }


        and aa.end_lawyer_id = #{ condition.lawyerId }


        and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }


        and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }


order by agree.gmt_create desc

select count(*)
FROM bus_agreement_audit aa
left join bus_agreement agree on agree.id = aa.agreement_id
left join bus_custom cus on cus.id = agree.custom_id
where aa.is_delete = 0

    and agree.state = #{ condition.state }



    and agree.name like #{ name }



        and aa.lawyer_id = #{ condition.lawyerId }


        and aa.end_lawyer_id = #{ condition.lawyerId }


        and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }


        and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }

SELECT agree.id as agreeId, agree.name as agreeName, agree.state as agreeState, agree.end_time as endTime, cus.name as customName FROM bus_agreement agree left join bus_custom cus on cus.id = agree.custom_id where agree.is_delete = 0
and agree.state = #{ condition.state }
and agree.name like #{ name }
order by agree.gmt_create desc

select count(*)
FROM bus_agreement agree
left join bus_custom cus on cus.id = agree.custom_id
where agree.is_delete = 0

    and agree.state = #{ condition.state }



    and agree.name like #{ name }

SELECT aa.id as agreementAuditId, aa.lawyer_id as firstLawyerId, aa.end_lawyer_id as endLawyerId, aa.state as getState, agree.id as agreeId, agree.name as agreeName, agree.state as agreeState, agree.end_time as endTime, ifnull(lawyer1.name,"") as firstLawyerName, ifnull(lawyer2.name,"") as endLawyerName, cus.name as customName FROM bus_agreement_audit aa left join bus_agreement agree on agree.id = aa.agreement_id left join bus_custom cus on cus.id = agree.custom_id left join bus_lawyer lawyer1 on lawyer1.id = aa.lawyer_id left join bus_lawyer lawyer2 on lawyer2.id = aa.end_lawyer_id where aa.is_delete = 0
and DATE_FORMAT(aa.gmt_create, '%Y-%m-%d') = #{ condition.gmtTime }
and agree.state = #{ condition.state }
and aa.lawyer_id = #{ condition.lawyerId }
order by agree.gmt_create desc

SELECT agree.id as agreeId, agree.name as agreeName, agree.state as agreeState, agree.end_time as endTime, DATE_FORMAT(agree.gmt_create, '%Y-%m-%d') as upTime, cus.name as customName, ifnull(aa.state,"") as getState, ifnull(aa.id,"") as agreementAuditId, ifnull(aa.end_lawyer_id,"") as endLawyerId, ifnull(aa.lawyer_id,"") as firstLawyerId, ifnull(lawyer1.name,"") as firstLawyerName, ifnull(lawyer2.name,"") as endLawyerName FROM bus_agreement agree left join bus_custom cus on cus.id = agree.custom_id left join bus_agreement_audit aa on aa.agreement_id = agree.id left join bus_lawyer lawyer1 on lawyer1.id = aa.lawyer_id left join bus_lawyer lawyer2 on lawyer2.id = aa.end_lawyer_id where agree.is_delete = 0
and agree.state = #{ condition.state }
and agree.name like #{ name }
order by agree.gmt_create desc
