mapper.select:
SELECT aa.id as agreementAuditId, aa.lawyer_id as firstLawyerId, aa.end_lawyer_id as endLawyerId, aa.state as getState, agree.id as agreeId, agree.name as agreeName, agree.state as agreeState, agree.end_time as endTime, cus.name as customName FROM bus_agreement_audit aa left join bus_agreement agree on agree.id = aa.agreement_id left join bus_custom cus on cus.id = agree.custom_id where aa.is_delete = 0
and agree.state = #{ condition.state }
and agree.name like #{ name }
and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }
order by agree.gmt_create desc

select count(*)
FROM bus_agreement_audit aa
left join bus_agreement agree on agree.id = aa.agreement_id
left join bus_custom cus on cus.id = agree.custom_id
where aa.is_delete = 0

    and agree.state = #{ condition.state }



    and agree.name like #{ name }


    and aa.lawyer_id = #{ condition.lawyerId } or aa.end_lawyer_id = #{ condition.lawyerId }
